changeSet.sql:
create sequence num;;
create table flora ( num bigint not null primary key, catalog varchar(255), collectedBy varchar(2000), familyCode varchar(1000), genusCode varchar(1000), typeCode varchar(1000), usageCode varchar(1000), regionCode varchar(1000), collectPlace varchar(2000), collectCoordinate varchar(100), collectAltitude varchar(100), collectDate timestamp, floraWeight varchar(100), behaviorPercent varchar(100) );;
create table table_of_dicts ( code varchar(1000) not null primary key, title varchar(3000), dictType varchar(255), parentCode varchar(255) );;
create table flora_collection_relation ( flora bigint not null, collectionDict varchar(1000) not null, measureDict varchar(1000) not null, actual smallint not null default 1,
PRIMARY KEY(flora, collectionDict, measureDict) );;

create table flora_usage_relation (
  flora bigint not null,
  usageDict varchar(1000) not null,
  actual smallint not null default 1,

  PRIMARY KEY(flora, usageDict)
);;

 insert into flora_usage_relation (flora, usageDict, actual)
    select num, usageCode, 1 from flora where usageCode is not null group by num, usageCode;;

alter table flora add column image BYTEA;;
alter table flora add column image_name varchar(1000);;
alter table flora add column description text;;
